import React from 'react';
import "./Main.css";
import { observer } from "mobx-react";
import MenuBar, { CurrentPage } from '../MenuBar/MenuBar';
import { action, observable } from 'mobx';
import { ColorScheme } from "../ColorScheme";
import Repositories from '../Repositories/Repositories';

interface LoadManager {
    MenuLoaded: boolean;
    RepositoriesLoaded: boolean;
}

@observer
export default class Main extends React.Component<{}> {
    @observable private repositories = React.createRef<Repositories>();
    @observable private loadManager: LoadManager = {
        MenuLoaded: false,
        RepositoriesLoaded: false
    }

    onMenuLoaded = action(() => {
        this.loadManager.MenuLoaded = true;
        const repositories = this.repositories.current;
        repositories && repositories.populate();
    })

    onRepositoriesLoaded = action(() => {
        this.loadManager.RepositoriesLoaded = true;
    });

    renderPage = () => {
        const currentPage = React.useContext(CurrentPage);
        return currentPage === 1 ? <Repositories
            ref={this.repositories}
            onLoad={this.onRepositoriesLoaded}
        /> : null
    }

    render() {
        const { MenuLoaded } = this.loadManager;
        return (
            <CurrentPage.Consumer>
                {currentPage => {
                    console.log("CURRPAGE", currentPage);
                    return (
                        <div
                            className={'outermost'}
                            style={{ background: MenuLoaded ? ColorScheme.primary : "white" }}
                        >
                            <MenuBar
                                onLoad={this.onMenuLoaded}
                                transitionDuration={1500}
                            />
                            <div
                                className={"body"}
                                style={{
                                    opacity: MenuLoaded ? 1 : 0,
                                }}
                            >
                                {this.renderPage()}
                            </div>
                        </div>
                    )
                }}
            </CurrentPage.Consumer>
        )
    }

}
