import React from 'react';
import { observer } from "mobx-react";
import { observable, action } from "mobx";
import "./MenuBar.css";

export interface MenuBarProps {
    transitionDuration: number;
}

@observer
export default class MenuBar extends React.Component<MenuBarProps> {
    @observable private textShiftExecuted = false;
    @observable private transitionExecuted = false;

    componentDidMount() {
        setTimeout(action(() => this.transitionExecuted = true), this.props.transitionDuration);
        setTimeout(action(() => this.textShiftExecuted = true), this.props.transitionDuration * 2 / 3);
    }

    render() {
        const title = (
            <span
                className={'samwilkins'}
                style={{ color: this.transitionExecuted ? "white" : "black" }}
            >
                {'{ samwilkins.me }'}
            </span>
        );
        return (
            <div
                className={'container'}
                style={{ height: this.transitionExecuted ? 60 : 0 }}
            >
                <div
                    className={'import-phrase'}
                    style={{ left: this.textShiftExecuted ? -80 : 20 }}
                >
                    <span className={'heading'}>import {title} from react-typescript</span>
                </div>
            </div>
        )
    }

}
