import React from 'react';
import { observer } from "mobx-react";
import { observable, action, runInAction } from "mobx";
import "./MenuBar.css";
import { ColorScheme } from '../ColorScheme';

export interface MenuBarProps {
    onLoad: () => void;
    transitionDuration: number;
}

const username = "samwilkins333";
const apiEndpoint = "https://api.github.com/user/repos";

@observer
export default class MenuBar extends React.Component<MenuBarProps> {
    @observable private textShiftExecuted = false;
    @observable private transitionExecuted = false;
    @observable private repositories: any[] = [];

    componentDidMount() {
        const { transitionDuration, onLoad } = this.props;
        setTimeout(action(() => this.textShiftExecuted = true), transitionDuration * 2 / 3);
        setTimeout(action(() => {
            this.transitionExecuted = true;
            onLoad();
        }), transitionDuration);
        this.dispatchGithubRequest();
    }

    dispatchGithubRequest = () => {
        const headers = { Authorization: `token ${process.env.REACT_APP_GITHUB_KEY}`};
        fetch(apiEndpoint, { headers }).then(async response => {
            const repositories= await response.json() as any[];
            runInAction(() => this.repositories = repositories.filter(repo => repo.full_name.startsWith(username)));
        });
    }

    render() {
        const title = (
            <span
                className={'samwilkins'}
                style={{ color: this.transitionExecuted ? "white" : ColorScheme.samwilkins }}
            >
                {'{ samwilkins.me }'}
            </span>
        );
        return (
            <div
                className={'container'}
                style={{ height: this.transitionExecuted ? 60 : 0 }}
            >
                <div
                    className={'import-phrase'}
                    style={{ left: this.textShiftExecuted ? -85 : 20 }}
                >
                    <span className={'heading'}>import {title} from react-typescript</span>
                </div>
            </div>
        )
    }

}
