import React from 'react';
import { observer } from "mobx-react";
import { observable, action } from "mobx";
import "./MenuBar.css";
import { ColorScheme } from '../ColorScheme';
import { Page } from '../Pages';

export interface MenuBarProps {
    onLoad: () => void;
    transitionDuration: number;
}

const DefaultCurrentPage = 0;
const CurrentPage = React.createContext<number>(DefaultCurrentPage);

@observer
export default class MenuBar extends React.Component<MenuBarProps> {
    @observable private textShiftExecuted = false;
    @observable private transitionExecuted = false;
    @observable private currentPage = DefaultCurrentPage;
    @observable private height = 0;
    @observable private expanded = false;
    @observable private interactedWith = false;

    componentDidMount() {
        const { transitionDuration, onLoad } = this.props;
        setTimeout(action(() => this.textShiftExecuted = true), transitionDuration * 2 / 3);
        setTimeout(action(() => {
            this.transitionExecuted = true;
            this.currentPage = 1;
            this.height = 60;
            onLoad();
        }), transitionDuration);
    }

    private get currentPageTitle() {
        return Page[this.currentPage].title;
    }

    showOthers = () => {
        if (!this.expanded) {
            return (null);
        }
        return (
            <div className={'menu-items heading'}>
                {Object.values(Page).filter(page => page.title !== this.currentPageTitle).map(page => (
                    <div
                        className={'page'}
                        key={page.title}
                        onClick={action(() => this.currentPage = Object.values(Page).indexOf(page))}
                    >{page.title}</div>
                ))}
            </div>
        )
    }

    render() {
        const title = (
            <span
                className={'samwilkins'}
                style={{ color: this.transitionExecuted ? "white" : ColorScheme.samwilkins }}
            >
                {`{ ${this.interactedWith ? this.currentPageTitle : 'samwilkins.me'} }`}
            </span>
        );
        return (
            <CurrentPage.Provider value={this.currentPage}>
                <div
                    className={'container'}
                    style={{
                        height: this.expanded ? window.screen.availHeight : this.height,
                        // transition: this.expanded || !this.transitionExecuted ? "0.2s ease all" : undefined
                    }}
                    onClick={action(() => {
                        this.expanded = !this.expanded;
                        this.interactedWith = true;
                    })}
                >
                    <div
                        className={'import-phrase'}
                        style={{ left: this.textShiftExecuted ? -85 : 20 }}
                    >
                        <span className={'heading'}>import {title} from react-typescript</span>
                    </div>
                    <div className={'white-hr'} style={{ opacity: this.expanded ? 0.5 : 0 }}></div>
                    {this.showOthers()}
                </div>
            </CurrentPage.Provider>
        )
    }

}
