import React from 'react';
import "./Repository.css";
import { observer } from 'mobx-react';
import privateIcon from '../private.png';
import { action, observable } from 'mobx';
import { FontWeightProperty } from 'csstype';

export interface RepositoryProps {
    model: any;
    marginTop: number;
}

@observer
export default class Repository extends React.Component<RepositoryProps> {
    @observable private color: string = "black";
    @observable private background: string = "white";

    backlink = () => {
        !this.props.model.private && window.open(`https://github.com/samwilkins333/${this.props.model.name}`);
    }

    setFontColor = action((color: string) => {
        this.color = color;
        this.background = color === "red" ? "rgb(255, 0, 0, 0.5)" : "white";
    })

    render() {
        console.log(this.props.model);
        const { name, private: isPrivate } = this.props.model;
        const marginTop = this.props.marginTop;
        const pending = marginTop === 0
        return (
            <div
                className={'repository'}
                onClick={this.backlink}
                onPointerEnter={() => this.setFontColor("red")}
                onPointerLeave={() => this.setFontColor("black")}
                style={{
                    marginTop,
                    marginLeft: pending ? 3 : marginTop,
                    marginRight: pending ? 3 : marginTop,
                    border: pending ? "none" : `1px solid ${this.color}`,
                    borderRadius: pending ? 0 : 5,
                    cursor: isPrivate ? "normal" : "pointer",
                    color: this.color,
                    background: this.background
                }}
            >
                <div>{name}</div>
                {isPrivate ? <img
                    src={privateIcon}
                    width={40}
                    className={'private'}
                /> : null}
            </div>
        );
    }

}
